import React from "react";
import { useState } from "react";
import { useEffect } from "react";

const IndexCofees = () => {

    const [cofeeList, setList] = useState("")

    const prepList = (list) => {
        setList(JSON.stringify(list))
    }

    const filter = () => {
        const form = document.getElementById('search-form')
        const formDatas = new FormData(form)
        setList(JSON.stringify(JSON.cofeeList.filter(elem => (elem.includes(Array.from(formDatas)[0][1])))))
    }

    useEffect(() => {
        fetch("https://mycoffees.herokuapp.com/products", {
            method: "get",
            headers: {
                "Content-Type": "application/json",
            }
        }).then((response) => { return (response.json()) })
            .then((response) => {prepList(response)
            console.log(response)
            console.log(cofeeList)})
    }, []);

    return (
        <>
            <form class="search-form" id="search-form" onSubmit={event => {event.preventDefault(); filter()}}>
                <input type="text" id="search-bar" name="search-bar"></input>
                <input type="submit" value="Rechercher"></input>
            </form>
            <div>
                {cofeeList.length > 1 ? JSON.parse(cofeeList).map(elem => (
                    <p>{elem.name}</p>
                )) : <></>}
            </div>
        </>
    )
}

export default IndexCofees
