            </form>
            <div className="cofee-list">
                {showList.length > 1 ? JSON.parse(cofeeList).map(product => (
                    <CofeeComponent key={product.id} product={product}/>
                )) : <></>}
            </div>
        </>
    )
}

export default IndexCofees
